import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const CategoryList = ({ categoryData }) => {
  const [iconCheckVisibles, setIconCheckVisibles] = useState(categoryData.map(() => false));

  const handleCategoryWithProductClick = (index) => {
    const newIconCheckVisibles = categoryData.map(() => false);
    newIconCheckVisibles[index] = true;
    setIconCheckVisibles(newIconCheckVisibles);
  };

  return (
    <Accordion.Item value="category-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Category List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel bg="gray.2">
        {categoryData.map((category, index) => (
          <Box
            key={index}
            mb="xs"
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(index)}
          >
            <Flex
              h={{ base: "auto", md: 100 }}
              justify="center"
              align="center"
              direction={{ base: "column", md: "row" }}
              gap="sm"
            >
              {category.images.map((image, imageIndex) => (
                <Flex key={imageIndex} justify="center" align="center" direction="column">
                  <Avatar
                    variant="filled"
                    radius="sm"
                    size="lg"
                    color="yellow"
                    src={image}
                  />
                  <Text size="xs">{category.label}</Text>
                </Flex>
              ))}
              {iconCheckVisibles[index] && (
                <Flex h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default CategoryList;
