import React from "react";
import { useState } from "react";
import {
  Container,
  Group,
  Burger,
  Center,
  Box,
  Text,
  Stack,
  Button,
} from "@mantine/core";
import { useDisclosure } from "@mantine/hooks";
import classes from "./HeaderMenu.module.css";

import {
  IconHome2,
  IconWorld,
  IconCategory,
  IconDatabase,
  IconGardenCart,
  IconTruckDelivery,
  IconUser,
  IconSettings,
} from "@tabler/icons-react";

const links = [
  {
    link: "#1",
    icon: <IconHome2 />,
    label: "Home",
  },
  {
    link: "#2",
    icon: <IconWorld />,
    label: "Global",
  },
  {
    link: "#3",
    icon: <IconCategory />,
    label: "Category",
  },
  {
    link: "#4",
    icon: <IconDatabase />,
    label: "Product List",
  },
  {
    link: "#5",
    icon: <IconGardenCart />,
    label: "Cart",
  },
  {
    link: "#6",
    icon: <IconTruckDelivery />,
    label: "Order",
  },
  {
    link: "#7",
    icon: <IconUser />,
    label: "Profile",
  },
  {
    link: "#8",
    icon: <IconSettings />,
    label: "Settings",
  },
];

const HeaderMenu = ({ onSelectMenuItem }) => {
  const [opened, { toggle }] = useDisclosure(false);
  const [active, setActive] = useState(links[0].link);

  const items = links.map((link) => (
    <a
      key={link.label}
      href={link.link}
      className={classes.link}
      data-active={active === link.link || undefined}
      onClick={(event) => {
        event.preventDefault();
        setActive(link.link);
        onSelectMenuItem(link.label); // Send the selected menu item to the App component
      }}
    >
      <Center>
        <Box style={{ display: "flex", alignItems: "center" }}>
          {link.icon}
          <span className={classes.linkLabel} style={{ marginLeft: "10px" }}>
            {link.label}
          </span>
        </Box>
      </Center>
    </a>
  ));

  return (
    <Container
      size="xl"
      bg="indigo.9"
      justify="space-around"
      className={classes.inner}
    >
          <Button justify="flex-start">Theam-1</Button>

      <Stack>
        {/* <Text size="xl" c={`#FFFFFF`}>Theam-1</Text> */}
        <Group ml="lg" gap="md" visibleFrom="xs" c={`#FFFFFF`}>
          {items}
        </Group>

        <Burger opened={opened} onClick={toggle} hiddenFrom="xs" size="sm" />
      </Stack>
    </Container>
  );
};

export default HeaderMenu;
