import {
  Avatar,
  Text,
  Flex,
  Stack,
  Autocomplete,
  Box,
  rem,
} from "@mantine/core";
import React from "react";
import {
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";
const AppBar = () => {
  const [isStack1Selected, setIsStack1Selected] = useState(false);
  const [isStack2Selected, setIsStack2Selected] = useState(false);

  const handleStack1Click = () => {
    setIsStack1Selected(!isStack1Selected);
  };

  const handleStack2Click = () => {
    setIsStack2Selected(!isStack2Selected);
  };
  return (
    <>
      <Stack bg="gray.3" onClick={handleStack1Click}>
        <Flex mih={40} gap="md" justify="space-between" align="center">
          <Box style={{ flex: 1 }}>
            <IconAlignLeft style={{ marginLeft: "4px" }} />
          </Box>
          <Box
            style={{
              display: "flex",
              alignItems: "center",
              justify: "space-around",
              gap: "xl",
              marginRight: "4px",
            }}
          >
            <Box style={{ marginRight: "10px" }}>
              <IconSearch size={`15px`} />
            </Box>
            <Box>
              <Avatar
                size="sm"
                bg="white"
                radius="md"
                src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
              />
            </Box>
          </Box>
        </Flex>
      </Stack>

      <Stack bg="gray.3" mt="xs">
        <Flex mih={40} gap="md" justify="space-between" align="center">
          <IconAlignLeft style={{ marginLeft: "4px" }} />
          <Box
            bg="gray.1"
            p={3}
            c={`gray.4`}
            style={{
              width: "150px",
              backgroundColor: "var(--mantine-color-gray-0)",
              border: "1px solid var(--mantine-color-gray-4)",
              borderRadius: "15px",
            }}
          >
            <Flex
              gap="xs"
              justify="flex-start"
              align="center"
              direction="row"
              wrap="wrap"
            >
              <IconSearch
                style={{ width: rem(16), height: rem(16) }}
                stroke={1.5}
              />
              <Text>Search</Text>
            </Flex>
          </Box>
          <Box
            style={{
              display: "flex",
              alignItems: "center",
              justify: "space-around",
              gap: "xl",
              marginRight: "4px",
            }}
          >
            <Box>
              <Avatar
                size="sm"
                bg="white"
                radius="xl"
                // src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
              >
                <IconBell size={`15px`} />
              </Avatar>
            </Box>
          </Box>
        </Flex>
      </Stack>
    </>
  );
};

export default AppBar;
