import React, { useState } from "react";
import Layout from "./Layout/Layout";
import { Grid } from "@mantine/core";
import ResponsivePhone from "../ResponsivePhone";
import CategoryCustomize from "./CategoryListCustomize/CategoryCustomize";
import CategoryCustomize2 from "./CategoryListCustomize/CategoryCustomize2";
import CategoryCustomize3 from "./CategoryListCustomize/CategoryCustomize3";
import CategoryCustomize4 from "./CategoryListCustomize/CategoryCustomize4";
import CategoryWithProductCustomize from "./CategoryWithProductCustomize/CategoryWithProductCustomize";
import CategoryWithProductCustomize2 from "./CategoryWithProductCustomize/CategoryWithProductCustomize2";

const Index = () => {
  const [categoryListBoxVisible, setCategoryListBoxVisible] = useState(false);
  const [categoryListBoxVisible2, setCategoryListBoxVisible2] = useState(false);
  const [categoryListBoxVisible3, setCategoryListBoxVisible3] = useState(false);
  const [categoryListBoxVisible4, setCategoryListBoxVisible4] = useState(false);
  const [categoryWithProductVisible, setCategoryWithProductVisible] =
    useState(false);
  const [categoryWithProductHeaderValue, setCategoryWithProductHeaderValue] =
    useState("Hot Sales");
  return (
    <Grid>
      <Grid.Col span={4} h={550} style={{ overflowY: "auto" }}>
        <Layout
          setCategoryListBoxVisible={setCategoryListBoxVisible}
          categoryListBoxVisible={categoryListBoxVisible}
          setCategoryListBoxVisible2={setCategoryListBoxVisible2}
          categoryListBoxVisible2={categoryListBoxVisible2}
          setCategoryListBoxVisible3={setCategoryListBoxVisible3}
          categoryListBoxVisible3={categoryListBoxVisible3}
          setCategoryListBoxVisible4={setCategoryListBoxVisible4}
          categoryListBoxVisible4={categoryListBoxVisible4}
          setCategoryWithProductVisible={setCategoryWithProductVisible}
          categoryWithProductVisible={categoryWithProductVisible}
          categoryWithProductHeaderValue={categoryWithProductHeaderValue}
        />
      </Grid.Col>
      <Grid.Col span={4} mt="xs">
        <ResponsivePhone />
      </Grid.Col>
      <Grid.Col span={4} h={550} style={{ overflowY: "auto" }}>
        <CategoryCustomize categoryListBoxVisible={categoryListBoxVisible} />
        <CategoryCustomize2 categoryListBoxVisible2={categoryListBoxVisible2} />
        <CategoryCustomize3 categoryListBoxVisible3={categoryListBoxVisible3} />
        <CategoryCustomize4 categoryListBoxVisible4={categoryListBoxVisible4} />
        <CategoryWithProductCustomize
          categoryWithProductVisible={categoryWithProductVisible}
          categoryWithProductHeaderValue={categoryWithProductHeaderValue}
          setCategoryWithProductHeaderValue={setCategoryWithProductHeaderValue}
        />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
