import { Stack, Text, ColorPicker, Flex, Slider, Input } from "@mantine/core";
import React, { useState } from "react";

const Layout3 = ({
  onColorChange,
  boxBorderRadius,
  onBorderRadiusChange,
  boxShadow,
  onBoxShadowChange,
  text,
  onTextChange,
  onTextBgColorChange,
  textBorderRadius,
  onTextBorderRadiusChange,
}) => {

  return (
    <Stack bg="gray.3">
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onColorChange(color);
          }}
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Box Border Radius</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            value={boxBorderRadius}
            onChange={onBorderRadiusChange}
            min={0}
            max={50}
            labelAlwaysOn
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Box Shadow</Text>
        <Input
          type="text"
          value={boxShadow}
          onChange={(event) => {
            onBoxShadowChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Text</Text>
        <Input
          type="text"
          value={text}
          onChange={(event) => {
            onTextChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onTextBgColorChange(color);
          }}
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Border Radius</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
        <Slider
          value={textBorderRadius}
          onChange={(newValue) => {
            onTextBorderRadiusChange(newValue);
          }}
          min={0}
          max={50}
          labelAlwaysOn
          style={{ width: "300px" }}
        />
        </Flex>
      </Stack>
    </Stack>
  );
};

export default Layout3;
