import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { IconChevronDown } from "@tabler/icons-react";
import { Link } from "react-router-dom"; // Import Link from React Router
import classes from "./css/HeaderMenu.module.css";

import {
  Menu,
  Group,
  Center,
  Burger,
  Container,
} from "@mantine/core";

// Define your menu links with paths
const links = [
  { path: "/", label: "Home" },
  { path: "/docs", label: "App Bar" },
  { path: "/resources", label: "Nav Bar" },
  { path: "/community", label: "Drawer" },
  // Add more links as needed
];

export default function HeaderMenu() {
  const [opened, { toggle }] = useDisclosure(false);

  // Map your links to Menu components with Link elements
  const items = links.map((link) => (
    <Menu.Item key={link.path}>
      <Link to={link.path} className={classes.link}>
        <Center>
          <span className={classes.linkLabel}>{link.label}</span>
          <IconChevronDown size="0.9rem" stroke={1.5} />
        </Center>
      </Link>
    </Menu.Item>
  ));

  return (
    <header className={classes.header}>
      <Container size="md">
        <div className={classes.inner}>
          <Group gap={5} visibleFrom="sm">
            {items}
          </Group>
          <Burger opened={opened} onClick={toggle} size="sm" hiddenFrom="sm" />
        </div>
      </Container>
    </header>
  );
}
