import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Checkbox,
  Grid,
} from "@mantine/core";
import React, { useState } from "react";
import ResponsivePhone from "../ResponsivePhone";

const categories = [
  {
    name: "Electronics",
    imageSrc:
      "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    name: "Accessories",
    imageSrc:
      "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    name: "Clothing",
    imageSrc:
      "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    name: "Shoes",
    imageSrc:
      "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const Index = () => {
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [flexBlock, setFlexBlock] = useState(null); // State to store the Flex block
  
    const handleCategoryClick = (index) => {
      setSelectedCategory(index);
  
      // Set the Flex block when a category is clicked
      setFlexBlock(
        <Flex
          h={{ base: "auto", md: 100 }}
          justify="center"
          align="center"
          direction={{ base: "column", md: "row" }}
          gap="sm"
        >
          {categories.map((category, i) => (
            <Flex
              key={i}
              justify="center"
              align="center"
              direction="column"
              onClick={() => handleCategoryClick(i)}
            >
              <Avatar
                variant="filled"
                radius="sm"
                size="lg"
                color="yellow"
                src={category.imageSrc}
              />
              <Text size="xs">{category.name}</Text>
            </Flex>
          ))}
          <Flex h={{ base: "auto", md: `100%` }}>
            <Checkbox defaultChecked />
          </Flex>
        </Flex>
      );
    };
  return (
    <Grid mt="xs" pb="xs">
      <Grid.Col span={4}>
        <Accordion>
          <Accordion.Item value="category-list">
            <Accordion.Control bg="indigo.2">
              <Center>
                <Text fw={700}>Category List</Text>
              </Center>
            </Accordion.Control>
            <Accordion.Panel bg="gray.2">
              <Box mb="xs" bg="#FFFFFF" style={{ cursor: "pointer" }}>
                <Flex
                  h={{ base: "auto", md: 100 }}
                  justify="center"
                  align="center"
                  direction={{ base: "column", md: "row" }}
                  gap="sm"
                >
                  {categories.map((category, index) => (
                    <Flex
                      key={index}
                      justify="center"
                      align="center"
                      direction="column"
                      onClick={() => handleCategoryClick(index)}
                    >
                      <Avatar
                        variant="filled"
                        radius="sm"
                        size="lg"
                        color="yellow"
                        src={category.imageSrc}
                      />
                      <Text size="xs">{category.name}</Text>
                    </Flex>
                  ))}
                  <Flex h={{ base: "auto", md: `100%` }}>
                    <Checkbox defaultChecked />
                  </Flex>
                </Flex>
              </Box>
            </Accordion.Panel>
          </Accordion.Item>
        </Accordion>
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone selectedCategory={selectedCategory} categories={categories} handleCategoryClick={handleCategoryClick} />
      </Grid.Col>
      <Grid.Col span={4}>3</Grid.Col>
    </Grid>
  );
};

export default Index;
