import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  ColorPicker,
  Avatar,
  Group,
  Divider,
  Accordion,
  rem,
  Checkbox,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconHome,
  IconHomeBolt,
  IconHomeCheck,
  IconHomeDown,
  IconCategory2,
  IconCategoryFilled,
  IconCategory,
  IconApps,
  IconAppsFilled,
  IconSearch,
  IconZoomFilled,
  IconZoomCode,
  IconZoomExclamation,
  IconWorldSearch,
  IconUsers,
  IconUsersPlus,
  IconBriefcase,
  IconPaperBag,
  IconUser,
  IconUserCircle,
  IconUserSquare,
  IconMessageCircle,
  IconShoppingBag,
  IconShoppingCartDown,
  IconShoppingCart,
  IconPhoto,
  IconPrinter,
  IconCameraSelfie,
} from "@tabler/icons-react";
const NavbarItem = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);
  const [selectedHomeIcon, setSelectedHomeIcon] = useState(null);
  const [selectedCatagoryIcon, setSelectedCatagoryIcon] = useState(null);
  const [selectedSearchIcon, setSelectedSearchIcon] = useState(null);
  const [selectedCartIcon, setSelectedCartIcon] = useState(null);
  const [selectedProfileIcon, setSelectedProfileIcon] = useState(null);

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const handleHomeIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedHomeIcon(index);
  };

  const handleCatagoryIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedCatagoryIcon(index);
  };

  const handleSearchIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedSearchIcon(index);
  };

  const handleCartIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedCartIcon(index);
  };

  const handleProfileIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedProfileIcon(index);
  };

  const icons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconSearch", component: <IconSearch /> },
    { name: "IconShoppingBag", component: <IconShoppingBag /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  const homeicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconHome", component: <IconHome /> },
    { name: "IconHomeBolt", component: <IconHomeBolt /> },
    { name: "IconHomeCheck", component: <IconHomeCheck /> },
    { name: "IconHomeDown", component: <IconHomeDown /> },
  ];

  const catagoryicons = [
    { name: "IconCategory2", component: <IconCategory2 /> },
    { name: "IconCategoryFilled", component: <IconCategoryFilled /> },
    { name: "IconCategory", component: <IconCategory /> },
    { name: "IconApps", component: <IconApps /> },
    { name: "IconAppsFilled", component: <IconAppsFilled /> },
  ];

  const searchicons = [
    { name: "IconSearch", component: <IconSearch /> },
    { name: "IconZoomFilled", component: <IconZoomFilled /> },
    { name: "IconZoomCode", component: <IconZoomCode /> },
    { name: "IconZoomExclamation", component: <IconZoomExclamation /> },
    { name: "IconWorldSearch", component: <IconWorldSearch /> },
  ];

  const carticons = [
    { name: "IconShoppingBag", component: <IconShoppingBag /> },
    { name: "IconBriefcase", component: <IconBriefcase /> },
    { name: "IconPaperBag", component: <IconPaperBag /> },
    { name: "IconShoppingCartDown", component: <IconShoppingCartDown /> },
    { name: "IconShoppingCart", component: <IconShoppingCart /> },
  ];

  const profileicons = [
    { name: "IconUser", component: <IconUser /> },
    { name: "IconUserCircle", component: <IconUserCircle /> },
    { name: "IconUserSquare", component: <IconUserSquare /> },
    { name: "IconUsers", component: <IconUsers /> },
    { name: "IconUsersPlus", component: <IconUsersPlus /> },
  ];
  return (
    <Stack bg="gray.3" radious="xs">
      <Stack bg="indigo.2">
        <Flex
          mih={50}
          bg="white"
          m="xs"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "25px" }}
        >
          {icons.map((icon, index) => (
            <ActionIcon
              radius="xl"
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${icon.component}`}
              onClick={() => handleIconClick(index, icon)}
            >
              {/* <Avatar>{icon.component}</Avatar> */}
              {icon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>
      {/* <Divider /> */}

      <Accordion ml="xs" mr="xs" variant="contained">
        <Accordion.Item value="navbaritems">
          <Accordion.Control>Navbar Items</Accordion.Control>
          <Accordion.Panel>
            <Stack>
              <Group gap="xs">
                <Checkbox ml="xs" />
                <IconHome2 />
                <Text>Home Icon</Text>
              </Group>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="space-around"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                {homeicons.map((homeicon, index) => (
                  <ActionIcon
                    radius="md"
                    key={index}
                    variant={selectedHomeIcon === index ? "outline" : "default"}
                    aria-label={`Icon ${homeicon.component}`}
                    onClick={() => handleHomeIconClick(index, homeicon)}
                  >
                    {homeicon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>

            <Stack mt="xs">
              <Group gap="xs">
                <Checkbox ml="xs" />
                <IconCategory2 />
                <Text>Category Icon</Text>
              </Group>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="space-around"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                {catagoryicons.map((catagoryicon, index) => (
                  <ActionIcon
                    radius="md"
                    key={index}
                    variant={
                      selectedCatagoryIcon === index ? "outline" : "default"
                    }
                    aria-label={`Icon ${catagoryicon.component}`}
                    onClick={() => handleCatagoryIconClick(index, catagoryicon)}
                  >
                    {catagoryicon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>

            <Stack mt="xs">
              <Group gap="xs">
                <Checkbox ml="xs" />
                <IconSearch />
                <Text>Search Icon</Text>
              </Group>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="space-around"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                {searchicons.map((searchicon, index) => (
                  <ActionIcon
                    radius="md"
                    key={index}
                    variant={
                      selectedSearchIcon === index ? "outline" : "default"
                    }
                    aria-label={`Icon ${searchicon.component}`}
                    onClick={() => handleSearchIconClick(index, searchicon)}
                  >
                    {searchicon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>

            <Stack mt="xs">
              <Group gap="xs">
                <Checkbox ml="xs" />
                <IconShoppingBag />
                <Text>Cart Icon</Text>
              </Group>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="space-around"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                {carticons.map((carticon, index) => (
                  <ActionIcon
                    radius="md"
                    key={index}
                    variant={selectedCartIcon === index ? "outline" : "default"}
                    aria-label={`Icon ${carticon.component}`}
                    onClick={() => handleCartIconClick(index, carticon)}
                  >
                    {carticon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>

            <Stack mt="xs">
              <Group gap="xs">
                <Checkbox ml="xs" />
                <IconUser />
                <Text>Profile Icon</Text>
              </Group>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="space-around"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                {profileicons.map((profileicon, index) => (
                  <ActionIcon
                    radius="md"
                    key={index}
                    variant={
                      selectedProfileIcon === index ? "outline" : "default"
                    }
                    aria-label={`Icon ${profileicon.component}`}
                    onClick={() => handleProfileIconClick(index, profileicon)}
                  >
                    {profileicon.component}
                  </ActionIcon>
                ))}
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="navbarsettings" mb="xs">
          <Accordion.Control>Navbar Settings</Accordion.Control>
          <Accordion.Panel>
            <Stack>
              <Stack>
                <Text ml="xs">Background Color</Text>
                <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
              </Stack>

              <Stack mt="xs">
                <Text ml="xs">Icon Color</Text>
                <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
              </Stack>

              <Stack mt="xs">
                <Text ml="xs">Selected Background Color</Text>
                <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
              </Stack>

              <Stack mt="xs">
                <Text ml="xs">Selected Icon Color</Text>
                <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
              </Stack>

              <Stack mb="xs">
        <Text ml="xs">Margin</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Flex
            mih={50}
            gap="md"
            ml="xs"
            justify="center"
            align="flex-start"
            direction="column"
            wrap="wrap"
          >
            <Text mt="xs">Left</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Right</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Top</Text>

            <Slider
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
            <Text>Bottom</Text>

            <Slider
              mb="xs"
              labelAlwaysOn
              value={value}
              onChange={handleChange}
              min={0}
              max={100}
              style={{ width: "300px" }}
            />
          </Flex>
        </Flex>
      </Stack>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>

        {/* <Accordion.Item value="camera">
          <Accordion.Control
            icon={
              <IconCameraSelfie
                style={{
                  color: "var(--mantine-color-teal-filled)",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Camera settings
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item> */}
      </Accordion>
    </Stack>
  );
};

export default NavbarItem;
