import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { AppShell, Burger, Group, Center, Image, Text } from "@mantine/core";
import { IconAlignJustified, IconCircleXFilled } from "@tabler/icons-react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom"; // Import Router and Route from react-router-dom
import Navbar from "./Navbar";
import Demo from "./Demo";

const Home = () => (
  <Center>
    <Image
      maw={740}
      mx="auto"
      radius="md"
      src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
      alt="Random image"
    />
  </Center>
);

const About = () => <Text>About Page</Text>; // Define your About page component
const Services = () => <Text>Services Page</Text>; // Define your Services page component
const Contact = () => <Text>Contact Page</Text>; // Define your Contact page component

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();
  
  return (
    <Router>
      <AppShell
        header={{ height: 50 }}
        navbar={{ width: 80, breakpoint: "sm", collapsed: { mobile: !opened } }}
        aside={{
          width: 300,
          breakpoint: "md",
          collapsed: { desktop: false, mobile: true },
        }}
        padding="md"
      >
        <AppShell.Header bg="#0000FF">
          <Group h="100%" px="md" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
              <div style={{ display: 'flex', alignItems: 'center', marginLeft: '8px' }}>
                <IconAlignJustified />
                <Text color="#ff5733" style={{ marginLeft: '4px' }}>Theme-1</Text>
              </div>
            </div>
            <div className="justyfi-end">
              <IconCircleXFilled />
            </div>
          </Group>
        </AppShell.Header>
        <AppShell.Navbar p="md">
          <Navbar />
        </AppShell.Navbar>
        <AppShell.Main>
          <Switch>
            <Route path="/" exact component={Home} />
            <Route path="/about" component={About} />
            <Route path="/services" component={Services} />
            <Route path="/contact" component={Contact} />
          </Switch>
        </AppShell.Main>
        <AppShell.Aside p="md">
          <Demo />
        </AppShell.Aside>
      </AppShell>
    </Router>
  );
};

export default AppShellDemo;
