import React, { useState } from "react";
import { Flex, Stack, Text, ActionIcon, Slider } from "@mantine/core";
import {
  IconCircleXFilled,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const TextTemplate = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleIconClick = (index, component) => {
    console.log("Clicked icon:", component);
    setSelectedIcon(index);
  };

  const textStyles = {
    normal: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "none",
    },
    bold: {
      fontWeight: "bold",
      fontStyle: "normal",
      textDecoration: "none",
    },
    italic: {
      fontWeight: "normal",
      fontStyle: "italic",
      textDecoration: "none",
    },
    underline: {
      fontWeight: "normal",
      fontStyle: "normal",
      textDecoration: "underline",
    },
  };

  const icons = [
    { name: "IconLetterN", component: <IconLetterN />, style: "normal" },
    { name: "IconBold", component: <IconBold />, style: "bold" },
    { name: "IconItalic", component: <IconItalic />, style: "italic" },
    { name: "IconUnderline", component: <IconUnderline />, style: "underline" },
  ];

  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Text
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack>
        <Text ml="xs">Title</Text>
        <Input
          ml="xs"
          mr="xs"
          placeholder="Please Enter Title"
          style={{ display: "inline-block" }}
        />
      </Stack>

      <Stack>
        <Text ml="xs">Background Color</Text>
        <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
      </Stack>

      <Stack>
        <Text ml="xs">Text Color</Text>
        <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
      </Stack>

      <Stack>
        <Text ml="xs">Size</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            my="xs"
            ml="xs"
            labelAlwaysOn
            value={value}
            onChange={handleChange}
            min={0}
