import React, { useState } from "react";
import { IconPlus, IconMinus } from "@tabler/icons-react";
import { Accordion } from "@mantine/core";

const Order = ({ items }) => {
  const [openItems, setOpenItems] = useState([]);

  const toggleChevronIcon = (index) => {
    const isOpen = openItems.includes(index);
    setOpenItems((prevOpenItems) =>
      isOpen ? prevOpenItems.filter((item) => item !== index) : [...prevOpenItems, index]
    );
  };

  const renderItems = (data, level = 0) => {
    return data.map((item, index) => (
      <Accordion.Item key={item.value} value={item.value} open={openItems.includes(index)}>
        <Accordion.Control
          icon={openItems.includes(index) ? <IconMinus /> : <IconPlus />}
          onClick={() => toggleChevronIcon(index)}
          style={{ marginLeft: `${level * 20}px` }} // Adjust the indentation as needed
        >
          {item.value}
        </Accordion.Control>
        <Accordion.Panel>{item.description}</Accordion.Panel>
        {item.subItems && renderItems(item.subItems, level + 1)}
      </Accordion.Item>
    ));
  };

  return <Accordion>{renderItems(items)}</Accordion>;
};

const App = () => {
  const groceries = [
    {
      emoji: "üçé",
      value: "Apples",
      description: "Crisp and refreshing fruit...",
      subItems: [
        {
          emoji: "üçè",
          value: "Green Apples",
          description: "Green apples are known for their tart flavor...",
        },
        {
          emoji: "üçé",
          value: "Red Apples",
          description: "Red apples are sweet and can be enjoyed as a snack or in desserts.",
        },
      ],
    },
    {
      emoji: "üçå",
      value: "Bananas",
      description: "Naturally sweet and potassium-rich fruit...",
    },
    {
      emoji: "ü•¶",
      value: "Broccoli",
      description: "Nutrient-packed green vegetable...",
    },
  ];

  return <Order items={groceries} />;
};

export default App;
