import { Box, Stack, Text, Center, Grid } from "@mantine/core";
import React from "react";

const Layout = ({ bgColor, borderRadius, columns }) => {
  const numberOfColumns = 4; // Change this to the desired number of columns
  const columnValues = [2, 3, 4, 2];
  return (
    <Box
      p={20}
      bg={bgColor}
      h={200}
      style={{
        borderRadius: `${borderRadius}px`,
      }}
    >
      <Stack spacing={0}>
        {/* <Grid ml="xs" mr="xs" >
          <Grid.Col span={columns}>
            <Box
              bg="blue.1"
              h={60}
              style={{
                border: "1px solid #000000",
              }}
            ></Box>
          </Grid.Col>
        </Grid> */}
        <Grid ml="xs" mr="xs">
          {columnValues.map((value, index) => (
            <Grid.Col key={index} span={value}>
              <Box
                bg="blue.1"
                h={60}
                style={{
                  border: "1px solid #000000",
                }}
              ></Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
