import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  ActionIcon,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
} from "@mantine/core";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import {
  IconCircleXFilled,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
} from "@tabler/icons-react";
const ImageTest = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "IconAlignCenter", component: <IconAlignCenter /> },
    { name: "IconAlignJustified", component: <IconAlignJustified /> },
    { name: "IconAlignRight", component: <IconAlignRight /> },
  ];
  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Image
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end", marginRight: "1rem" }}>
          <IconCircleXFilled />
        </div>
      </Flex>
    </Stack>
  );
};

export default ImageTest;
