import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";

import {
  IconX,
} from "@tabler/icons-react";
import CustomizeCategoryList2 from "./CustomizeCategoryList2";
import Settings from "./Settings";

const CategoryCustomize2 = ({categoryListBoxVisible2}) => {
  return (
    <>
    {categoryListBoxVisible2 &&(
    <Box m="xs" h={`fit-content`} bg="gray.2" style={{ borderRadius: "10px" }}>
      
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <Text
          fw={900}
          c={`#FFFFFF`}
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Customize
        </Text>
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
          }}
        />
      </Box>
      <Accordion
        m="xs"
        variant="contained"
        defaultValue="category-item"
        pb={10}
      >
        <Accordion.Item value="category-item">
          <Accordion.Control bg="#FFFFFF">
            <Text>Category Item</Text>
          </Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            <CustomizeCategoryList2 />
          </Accordion.Panel>
        </Accordion.Item>
        <Settings />
      </Accordion>
    </Box>
    )}
    </>
  );
};

export default CategoryCustomize2;
