import React, { useState } from "react";
import { Box, Input, Button } from "@mantine/core";

const Layout3 = ({ inputValue, onInputChange, onSubmit }) => {
  const [submittedValue, setSubmittedValue] = useState(""); // State to store the submitted value

  const handleInputSubmit = () => {
    setSubmittedValue(inputValue); // Store the submitted input
    onSubmit(); // Call the onSubmit callback function
  };

  return (
    <Box>
      <Input
        value={inputValue}
        onChange={(event) => onInputChange(event.target.value)}
        placeholder="Type something..."
      />
      <Button onClick={handleInputSubmit}>Submit</Button> {/* Add a submit button */}
    </Box>
  );
};

export default Layout3;
