// import { Grid, Text } from '@mantine/core'
import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  ColorPicker,
  Avatar,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
} from "@tabler/icons-react";

const Navbar = () => {
    const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconSearch", component: <IconSearch /> },
    { name: "AlignJustified", component: <IconAlignJustified /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Navbar
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack m="xs" bg="indigo.2">
        <Flex
          mih={50}
          bg="white"
          m="xs"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "25px" }}
        >
          <Avatar color="indigo" >
            <IconHome2 />
          </Avatar>
          <Avatar>
            <IconMessageCircle />
          </Avatar>
          <Avatar>
            <IconSearch />
          </Avatar>
          <Avatar>
            <IconShoppingBag />
          </Avatar>
          <Avatar>
            <IconUser />
          </Avatar>
        </Flex>
      </Stack>
    </Stack>
  );
};

export default Navbar;
