import React, { useState } from "react";
import { MantineProvider, createTheme } from "@mantine/core";
import "@mantine/core/styles.css";
// import Home from "./views/components/Home";
import Index from "./views/components/Global/index"
import HeaderMenu from "./views/components/Header/HeaderMenu";
import HomeIndex from "./views/components/Home/Index"
import HomeIndex2 from "./views/components/Home2/Index"

const theme = createTheme({
  fontFamily: "Open Sans, sans-serif",
  primaryColor: "cyan",
});

function App() {
  const [selectedMenuItem, setSelectedMenuItem] = useState(null);

  return (
    <MantineProvider theme={theme}>
      <HeaderMenu onSelectMenuItem={(item) => setSelectedMenuItem(item)} />
      {selectedMenuItem === "Home" && <HomeIndex />}
      {selectedMenuItem === "Global" && <Index />}
    </MantineProvider>
  );
}

export default App;
