import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  Container,
  Box,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
} from "@mantine/core";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";
import {
  IconCircleXFilled,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
} from "@tabler/icons-react";
const ImageTest = () => {
  const [value, setValue] = useState(1);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };
  return (
    <Stack bg="gray.3">
      <Container
        fluid
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Image
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Image</Text>
        <Dropzone
          bg="white"
          h="5rem"
          w="22rem"
          ml="xs"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Text mt="1.5rem" ta="center">
            Select Image
          </Text>
        </Dropzone>
      </Stack>

      <Stack m="xs">
        <Text style={{ marginLeft: "1rem" }}>Hight</Text>
