import { Box, Center, Flex, Grid, Stack, Text } from "@mantine/core";
import React, { useState } from "react";

const componentStyles = {
  box: {
    borderRadius: "10px",
    background: "#CED4DA",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    height: 200,
  },
  headerBox: {
    background: "#94D82D",
    borderRadius: "10px 10px 0 0",
    cursor: "pointer",
  },
  itemBox: {
    background: "#D0EBFF",
    border: "1px solid #000000",
    cursor: "pointer",
    height: 30,
  },
};

const Layout = ({ selected, onItemSelect }) => {
  const items = ["Item 1", "Item 2", "Item 3"];

  const [isTextBoxSelected, setTextBoxSelected] = useState(false);

  const toggleTextBoxSelection = () => {
    setTextBoxSelected(!isTextBoxSelected);
  };
  
  return (
    <Box style={{ borderRadius: "10px" }}>
      <Stack spacing={0}>
        <Box
          bg={isTextBoxSelected ? "blue.5" : "lime.5"}
          style={{ borderRadius: "10px 10px 0 0", cursor: "pointer" }}
          onClick={toggleTextBoxSelection}
        >
          <Center>
            <Text>Test</Text>
          </Center>
        </Box>
        <Grid ml="xs" mr="xs">
          {items.map((item, index) => (
            <Grid.Col span={4} key={index}>
              <Box
                bg={selected === item ? "blue.1" : "white"}
                h={60}
                style={{ border: "1px solid #000000", cursor: "pointer" }}
                onClick={() => onItemSelect(item)}
              >
                <Center>{item}</Center>
              </Box>
            </Grid.Col>
          ))}
        </Grid>
      </Stack>
    </Box>
  );
};

export default Layout;
