import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
} from "@mantine/core";

import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";
import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";

const CustomizeCategoryList4 = () => {
  const [files, setFiles] = useState([]);

  const [categoryHeaders, setCategoryHeaders] = useState({
    technology_level: "Technology",
    fashion_level: "Fashion",
    sports_level: "Sports",
    home_level: "Home",
  });

  const updateProperty = (property, value) => {
    setCategoryHeaders({ ...categoryHeaders, [property]: value });
  };

  const handleCategoryChange = (category, text) => {
    updateProperty(`${category}_level`, text);
  };

  const categories = [
    { value: "technology", label: "Technology" },
    { value: "fashion", label: "Fashion" },
    { value: "sports", label: "Sports" },
    { value: "home", label: "Home" },
  ];

  const accordionItems = categories.map((category, index) => (
    <Accordion.Item key={index} value={category.value}>
      <Accordion.Control bg="gray.2" direction="row">
        <Flex justify="flex-start" align="center" direction="row" gap="md">
          <Checkbox defaultChecked />
          <Text>{category.label}</Text>
        </Flex>
      </Accordion.Control>
      <Accordion.Panel>
        <Flex direction="column">
          <Text mt="xs">Background Color</Text>
          <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
        </Flex>
        <Flex direction="column">
          <Text mt="xs">Level</Text>
          <Input
            placeholder="Input component"
            value={categoryHeaders[`${category.value}_level`]}
            onChange={(event) => {
              handleCategoryChange(category.value, event.target.value);
            }}
          />
        </Flex>
      </Accordion.Panel>
    </Accordion.Item>
  ));

  return (
    <Accordion m="xs" variant="contained">
      {accordionItems}

      {/* Add More */}
      <Accordion.Item mt="xs" value="addmore">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Text c={`indigo.6`}>+ Add More</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text>Background Color</Text>
            <ColorPicker w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text>Level</Text>
            <Input placeholder="Input component" />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategoryList4;
