import React, { useState } from "react";
import {
  useDisclosure,
  AppShell,
  Burger,
  Group,
  Text,
  Menu,
  Center,
  Image,
} from "@mantine/core";
import Header from "./Header";
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom"; // Import React Router components

// Define components for each menu item (replace with your actual components)
const Home = () => <div>Home Component</div>;
const Documentation = () => <div>Documentation Component</div>;
const Resources = () => <div>Resources Component</div>;
const Community = () => <div>Community Component</div>;
const Blog = () => <div>Blog Component</div>;

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "var(--mantine-color-blue-4)",
    borderRadius: "0.5rem",
    padding: "0.5rem",
  };

  return (
    <Router> {/* Wrap your component with the Router */}
      <AppShell
        header={{ height: 90 }}
        padding="md"
        navbar={{ width: 300, breakpoint: "sm", collapsed: { mobile: !opened } }}
        aside={{
          width: 300,
          breakpoint: "md",
          collapsed: { desktop: false, mobile: true },
        }}
      >
        <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
          <AppShell.Header style={headerStyles}>
            <Group h="40%" px="md">
              <Text>Them-1</Text>
            </Group>
            <Group h="60%" px="md">
              <Header />
            </Group>
            <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
          </AppShell.Header>
        </div>
        <AppShell.Navbar p="md">
          <Switch>
            {/* Define routes for each menu item */}
            <Route path="/docs" component={Documentation} />
            <Route path="/resources" component={Resources} />
            <Route path="/community" component={Community} />
            <Route path="/blog" component={Blog} />
            <Route path="/" component={Home} /> {/* Default route */}
          </Switch>
        </AppShell.Navbar>
        <AppShell.Main>
          <Center>
            <Image
              maw={740}
              mx="auto"
              radius="md"
              src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
              alt="Random image"
            />
          </Center>
        </AppShell.Main>
        <AppShell.Aside p="md"></AppShell.Aside>
      </AppShell>
    </Router>
  );
};

export default AppShellDemo;
