import { Box,  Text, Grid } from "@mantine/core";
import {
    IconPlus,
    IconX,
    IconColumns1,
    IconBold,
    IconUnderline,
    IconItalic,
    IconLetterN,
  } from "@tabler/icons-react";
import React from "react";
import ResponsivePhone from "../ResponsivePhone";

const Index = () => {
  return (
    <Grid mt="xs" pb="xs">
      <Grid.Col span={4}>
        <Box
          bg="indigo.5"
          ml="xs"
          mr="xs"
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            backgroundColor: "indigo.5",
            padding: "10px",
            borderRadius: "10px",
          }}
        >
          <Text
            fw={900}
            c={`#FFFFFF`}
            style={{
              flex: "1",
              textAlign: "center",
            }}
          >
            Layouts
          </Text>
          <IconX
            size={28}
            // onClick={handleLayoutClick}
            style={{
              borderRadius: "50%",
              background: "red",
              color: "white",
              alignSelf: "flex-end",
              cursor: "pointer",
            }}
          />
        </Box>
        <Accordion m="xs" pb={10} variant="contained"></Accordion>
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone />
      </Grid.Col>
      <Grid.Col span={4}>3</Grid.Col>
    </Grid>
  );
};

export default Index;
