import React, { useState } from "react";
import {
  Flex,
  Accordion,
  ActionIcon,
} from "@mantine/core";
import {
  IconColumns1,
} from "@tabler/icons-react";

const CustomizeCategory = () => {
  const [selectionStates, setSelectionStates] = useState([
    true,
    false,
    false,
    false,
    false,
    false,
  ]);

  const toggleSelection = (index) => {
    const newSelectionStates = selectionStates.map((state, i) =>
      i === index ? true : false
    );
    setSelectionStates(newSelectionStates);
  };

  const accordionItems = [
    {
      title: "Type",
      icons: [
        { w: 150, h: 50 },
        { w: 100, h: 50, ml: "xs" },
        { w: 50, h: 50, ml: "xs" },
        { w: 150, h: 100 },
        { w: 100, h: 100, ml: "xs" },
        { w: 50, h: 100, ml: "xs" },
      ],
    },
    // Add more items as needed
  ];

  return (
    <Accordion>
      {accordionItems.map((item, index) => (
        <Accordion.Item key={index} value={`type-${index}`}>
          <Accordion.Control bg="#FFFFFF">{item.title}</Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            {item.icons.map((icon, i) => (
              <ActionIcon
                key={i}
                variant={selectionStates[i] ? "filled" : "default"}
                onClick={() => toggleSelection(i)}
                color="indigo"
                {...icon}
              >
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </ActionIcon>
            ))}
          </Accordion.Panel>
        </Accordion.Item>
      ))}
    </Accordion>
  );
};

export default CustomizeCategory;
