import React, { useState } from "react";
import {
  Box,
  Grid,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
  Button,
  ColorPicker,
  Input,
  Stack,
  Image,
  Card,
  Slider,
  ActionIcon,
} from "@mantine/core";
import {
  IconPlus,
  IconX,
  IconColumns1,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const CustomizeCategory = () => {

  const [selectionStates, setSelectionStates] = useState([
    true,
    false,
    false,
    false,
    false,
    false,
  ]);

  const toggleSelection = (index) => {
    const newSelectionStates = selectionStates.map((state, i) =>
      i === index ? true : false
    );
    setSelectionStates(newSelectionStates);
  };
  return (
    <>
      <Accordion.Item value="type">
        <Accordion.Control bg="#FFFFFF">Type</Accordion.Control>
        <Accordion.Panel bg="#FFFFFF">
          <ActionIcon
            variant={selectionStates[0] ? "filled" : "default"}
            onClick={() => toggleSelection(0)}
            color="indigo"
            w={150}
            h={50}
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[1] ? "filled" : "default"}
            onClick={() => toggleSelection(1)}
            color="indigo"
            w={100}
            h={50}
            ml="xs"
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[2] ? "filled" : "default"}
            onClick={() => toggleSelection(2)}
            color="indigo"
            w={50}
            h={50}
            ml="xs"
          >
            <IconColumns1
              style={{ width: "100%", height: "100%" }}
              stroke={1}
            />
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[3] ? "filled" : "default"}
            onClick={() => toggleSelection(3)}
            color="indigo"
            w={150}
            h={100}
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[4] ? "filled" : "default"}
            onClick={() => toggleSelection(4)}
            color="indigo"
            w={100}
            h={100}
            ml="xs"
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>

          <ActionIcon
            variant={selectionStates[5] ? "filled" : "default"}
            onClick={() => toggleSelection(5)}
            color="indigo"
            w={50}
            h={100}
            ml="xs"
          >
            <Stack w="100%" gap={0}>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
              <Flex>
                <IconColumns1
                  style={{ width: "100%", height: "100%" }}
                  stroke={1}
                />
              </Flex>
            </Stack>
          </ActionIcon>
        </Accordion.Panel>
      </Accordion.Item>
    </>
  );
};

export default CustomizeCategory;
