import {
  Avatar,
  Container,
  Flex,
  Stack,
  Autocomplete,
  Box,
  Button,
} from "@mantine/core";
import React, { useState } from "react";
import {
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";
const AppBar = () => {
  const [isSelected, setIsSelected] = useState(false);

  const handleContainerClick = () => {
    setIsSelected(!isSelected);
  };

  return (
    <Flex
      mih={50}
      bg="rgba(0, 0, 0, .3)"
      gap="md"
      justify="center"
      align="center"
      direction="column"
      wrap="wrap"
      radius="xl"
    >
      <Container
        fluid
        bg="indigo"
        style={{
          borderRadius: "5px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
          height: "40px"
        }}
      >
        <div style={{ display: "inline-block", justifyContent: "flex-center" }}>
          App Bars
        </div>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
        
                <IconCircleXFilled color="red" />
        </div>
      </Container>
      {/* <Stack
        style={{ display: "flex", alignItems: "center", width: "100%" }}
        bg="var(--mantine-color-blue-light)"
      >
        <div style={{ flex: 1 }}>App Bars</div>
        <div style={{ alignSelf: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Stack> */}

      <Button mb="xs">Button 3</Button>
    </Flex>
  );
};

export default AppBar;
