import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
} from "@tabler/icons-react";

const ImageTest = () => {
  const [selectedAlignment, setSelectedAlignment] = useState("center");

  const handleIconClick = (alignment) => {
    console.log("Clicked alignment:", alignment);
    setSelectedAlignment(alignment);
  };

  const alignments = [
    { name: "Center", alignment: "center" },
    { name: "Justified", alignment: "justified" },
    { name: "Right", alignment: "right" },
  ];

  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Image
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Alignment</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {alignments.map((alignmentObj, index) => (
            <ActionIcon
              key={index}
              variant={selectedAlignment === alignmentObj.alignment ? "outline" : "default"}
              aria-label={`Alignment: ${alignmentObj.name}`}
              onClick={() => handleIconClick(alignmentObj.alignment)}
            >
              {alignmentObj.name}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>

      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Image</Text>
        <img
          src="your-image-source-here.jpg" // Replace with your image source
          alt="Your Image"
          className={`alignment-${selectedAlignment}`}
        />
      </Stack>
    </Stack>
  );
};

export default ImageTest;
