import React, { useState } from "react";
import { Text, Accordion, ActionIcon, ColorPicker, Input, Stack, Checkbox, Flex, Slider } from "@mantine/core";
import { IconPlus, IconColumns1 } from "@tabler/icons-react";

const CustomizeItem = ({ title, showEdit, onEditClick, onIncrement, value, onChange }) => (
  <Stack>
    <Flex justify="space-between" align="center" wrap="wrap">
      <Text>{title}</Text>
      <Checkbox onClick={onEditClick} checked={showEdit} />
    </Flex>
    {showEdit && (
      <Stack gap={0} style={{ border: "1px solid #000", borderRadius: "10px" }}>
        <Flex ml="xs" mr="xs" direction="column">
          <Text>Text Color</Text>
          <ColorPicker w="auto" size="lg" format="rgba" />
        </Flex>
        <Stack ml="xs" mr="xs" gap={0}>
          <Text>Text Size</Text>
          <Flex mih={50} bg="white" gap="md" justify="center" align="center" direction="row" wrap="wrap">
            <Input type="number" value={value} size="xs" w={45} readOnly style={{ borderRadius: "20px" }} />
            <Slider value={value} onChange={onChange} min={0} max={20} w={190} label={null} />
            <IconPlus onClick={onIncrement} style={{ border: "1px solid #000", borderRadius: "50%", cursor: "pointer" }} />
          </Flex>
        </Stack>
        <Flex ml="xs" mr="xs" direction="column">
          <Text>Text Font</Text>
          <Flex bg="#FFFFFF" h={50} style={{ border: "1px solid #000", borderRadius: "10px" }}></Flex>
        </Flex>
        <Stack gap={0}>
          <Text ml="xs">Text Style</Text>
          {/* Add your style icons here */}
        </Stack>
      </Stack>
    )}
  </Stack>
);

const CustomizeCategory = () => {
  const [showProductNameEdit, setShowProductNameEdit] = useState(false);
  const [productNameTextSizeValue, setProductNameTextSizeValue] = useState(1);

  const toggleEditProduct = () => {
    setShowProductNameEdit(!showProductNameEdit);
  };

  return (
    <Accordion>
      <Accordion.Item value="product">
        <Accordion.Control bg="#FFFFFF">Product</Accordion.Control>
        <Accordion.Panel bg="#FFFFFF">
          <CustomizeItem
            title="Product Name"
            showEdit={showProductNameEdit}
            onEditClick={toggleEditProduct}
            value={productNameTextSizeValue}
            onChange={setProductNameTextSizeValue}
            onIncrement={() => setProductNameTextSizeValue(productNameTextSizeValue + 1)}
          />
          {/* Add more items here */}
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategory;
