import React, { useState } from "react";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Center>
      <Box
        style={{
          border: "2px solid #1b1c1b",
          borderRadius: "15px",
          width: "375px", // Adjust width to match your desired mobile screen size
          height: "667px", // Adjust height to match your desired mobile screen size
          backgroundColor: "#ffffff",
          boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {/* Your content goes here */}
        <Flex direction="column" align="center" justify="center" style={{ flex: 1 }}>
          {/* Place your content inside the mobile frame */}
          <Stack spacing="xl">
            {/* Add your components here */}
          </Stack>
        </Flex>
      </Box>
    </Center>
  );
};

export default ResponsivePhone;
