import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const brandData = [
  {
    id: 1,
    name: "Electronics",
    src: "https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png",
  },
  {
    id: 2,
    name: "Accessories",
    src: "https://hnsfpau.imgix.net/5/images/detailed/240/TH-32JS610Z.01.jpg?fit=fill&bg=0FFF&w=1500&h=844&auto=format,compress",
  },
  {
    id: 3,
    name: "Clothing",
    src: "https://i0.wp.com/post.healthline.com/wp-content/uploads/2020/07/Best_Baby_Clothes_1296x728.png?w=1155&h=1528",
  },
  {
    id: 4,
    name: "Shoes",
    src: "https://media.gq.com/photos/63eba1b2275d2fef78a425c2/16:9/w_2560%2Cc_limit/nike-running-shoes-streakfly-invincible.jpg",
  },
];

const categoryData = [
  {
    id: 1,
    name: "Electronics",
  },
  {
    id: 2,
    name: "Accessories",
  },
  {
    id: 3,
    name: "Clothing",
  },
  {
    id: 4,
    name: "Shoes",
  },
];

const BrandList = () => {
  const [boxVisible, setBoxVisible] = useState(false);
  const [iconCheckVisible, setIconCheckVisible] = useState(false);

  const handleCategoryWithProductClick = (id) => {
    setBoxVisible(!boxVisible);
    setIconCheckVisible(!iconCheckVisible);
  };

  return (
    <Accordion.Item value="brand-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Brand List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel>
        {brandData.map((brand) => (
          <Box
            key={brand.id}
            mb="xs"
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(brand.id)}
          >
            <Flex
              h={{ base: "auto", md: 100 }}
              justify="center"
              align="center"
              direction={{ base: "column", md: "row" }}
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color="yellow"
                  src={brand.src}
                />
                <Text size="xs">{brand.name}</Text>
              </Flex>
              {iconCheckVisible && (
                <Flex h={{ base: "auto", md: `100%` }}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
        {categoryData.map((category) => (
          <Box
            key={category.id}
            bg="dark"
            mt="xs"
            style={{ cursor: "pointer" }}
            onClick={() => handleCategoryWithProductClick(category.id)}
          >
            <Flex h={50} justify="center" align="center" direction="row" gap="sm">
              <Flex gap="sm" justify="center" align="center" direction="row">
                <Badge size="xs" color="blue">
                  {category.name}
                </Badge>
              </Flex>
              {iconCheckVisible && (
                <Flex h={`100%`}>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        ))}
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default BrandList;
