import React from "react";
import { Box, Paper } from "@mantine/core";

const MobileFrame = ({ children }) => {
  return (
    <Box
      style={{
        width: '375px', // Adjust this width to match your desired mobile screen size
        height: '667px', // Adjust this height to match your desired mobile screen size
        margin: '0 auto', // Center the frame horizontally
        position: 'relative',
        border: '1px solid #000', // Border to mimic phone frame
        borderRadius: '16px', // Rounded corners for phone frame
        overflow: 'hidden', // Clip content within the frame
      }}
    >
      <Box
        style={{
          width: '100%',
          height: '100%',
          border: '1px solid #000', // Border to mimic the phone screen
          borderRadius: 'inherit', // Inherit border-radius for inner content
          backgroundColor: '#000', // Background color to mimic the phone screen
          position: 'absolute',
          top: '10px', // Adjust the top position to fit your design
          bottom: '10px', // Adjust the bottom position to fit your design
          left: '10px', // Adjust the left position to fit your design
          right: '10px', // Adjust the right position to fit your design
          overflow: 'hidden', // Clip content within the screen
        }}
      >
        <Paper padding="md" style={{ height: '100%', borderRadius: 'inherit' }} shadow="xs">
          {children}
        </Paper>
      </Box>
    </Box>
  );
};

export default MobileFrame;
