import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  Input,
  ColorPicker,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconBold,
  IconUnderline,
  IconItalic,
  IconLetterN,
} from "@tabler/icons-react";

const TextTemplate = () => {
  const [selectedStyle, setSelectedStyle] = useState("normal"); // Default to normal text

  const handleChangeStyle = (style) => {
    setSelectedStyle(style);
  };

  const handleIconClick = (style) => {
    handleChangeStyle(style);
  };

  const styles = [
    { name: "Normal", style: "normal" },
    { name: "Bold", style: "bold" },
    { name: "Italic", style: "italic" },
    { name: "Underline", style: "underline" },
  ];

  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Text
        </Text>
        <div style={{ display: "flex", justifyContent: "flex-end", marginRight: "1rem" }}>
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack>
        <Text ml="xs">Text Style</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {styles.map((styleObj, index) => (
            <ActionIcon
              key={index}
              variant={selectedStyle === styleObj.style ? "outline" : "default"}
              aria-label={`Style: ${styleObj.name}`}
              onClick={() => handleIconClick(styleObj.style)}
            >
              {styleObj.name}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>

      <Stack>
        <Text ml="xs">Text Content</Text>
        <Text
          ml="xs"
          style={{
            fontWeight: selectedStyle === "bold" ? "bold" : "normal",
            fontStyle: selectedStyle === "italic" ? "italic" : "normal",
            textDecoration: selectedStyle === "underline" ? "underline" : "none",
          }}
        >
          Your text content goes here.
        </Text>
      </Stack>
    </Stack>
  );
};

export default TextTemplate;
