import React, { useState } from "react";
import {
  Stack,
  Text,
  ColorPicker,
  Flex,
  Slider,
  Input,
  Accordion,
  rem,
} from "@mantine/core";

const Layout3 = ({
  onColorChange,
  boxBorderRadius,
  onBorderRadiusChange,
  columns,
  onColumnsChange,
}) => {
  return (
    <Stack bg="gray.3">
      <Accordion>
        <Accordion.Item value="box">
          <Accordion.Control bg="indigo.2">Box</Accordion.Control>
          <Accordion.Panel>
            <Stack>
              <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
              <ColorPicker
                ml="xs"
                mr="xs"
                mb="xs"
                w="auto"
                size="lg"
                format="rgba"
                onChange={(color) => {
                  onColorChange(color);
                }}
              />
            </Stack>
            <Stack>
              <Text style={{ marginLeft: "1rem" }}>Box Border Radius</Text>
              <Flex
                mih={50}
                bg="white"
                ml="xs"
                mr="xs"
                gap="md"
                justify="center"
                align="center"
                direction="row"
                wrap="wrap"
                style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
              >
                <Slider
                  value={boxBorderRadius}
                  onChange={onBorderRadiusChange}
                  min={0}
                  max={50}
                  labelAlwaysOn
                  style={{ width: "300px" }}
                />
              </Flex>
            </Stack>
          </Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="item">
          <Accordion.Control bg="indigo.2">Item Box</Accordion.Control>
          <Accordion.Panel>
          
          </Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Stack>
  );
};

export default Layout3;
