import React from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  AppShell,
  Burger,
  Group,
  Skeleton,
  Text,
  Menu,
  MenuItem,
  Center,
  Image,
} from "@mantine/core";
import Header from "./Header";
import AppBar from "./Global/AppBar";
import SideAppBar from "./Global/SideAppBar";
import Drawer from "./Drawer/Drawer";
import ImageTest from "./Drawer/ImageTest";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: "var(--mantine-color-blue-4)", // Set the background color to indigo
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  return (
    <AppShell
      header={{ height: 90 }}
      padding="md"
      // footer={{ height: 60 }}
      navbar={{ width: 400, breakpoint: "sm", collapsed: { mobile: !opened } }}
      aside={{
        width: 400,
        breakpoint: "md",
        collapsed: { desktop: false, mobile: true },
      }}
    >
      <div  className="rounded-lg p-2">
        <AppShell.Header bg="indigo.6" style={headerStyles}>
          <Group h="40%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="60%" px="md">
            <Header />
          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
      </div>
      <AppShell.Navbar p="md">
        <ImageTest />
      </AppShell.Navbar>
      <AppShell.Main>
        <Center>
          <Image
            maw={740}
            mx="auto"
            radius="md"
            src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
            alt="Random image"
          />
        </Center>
        {/* <AppBar /> */}
      </AppShell.Main>
      <AppShell.Aside p="md">
        <Drawer />
      </AppShell.Aside>
    </AppShell>
  );
};

export default AppShellDemo;
