import React from "react";
import { useDisclosure } from "@mantine/hooks";
import {
  AppShell,
  Box,
  Burger,
  Button,
  Group,
  Skeleton,
  Autocomplete,
  Center,
  Image,
  Text,
} from "@mantine/core";
import {
  IconAlignJustified,
  IconCircleXFilled,
  IconAlignLeft,
  IconSearch,
  IconBellFilled,
  IconX,
} from "@tabler/icons-react";
import NavbarMinimal from "./NavbarMinimal";
import Demo from "./Demo";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();
  return (
    <AppShell
      header={{ height: 50 }}
      navbar={{ width: 80, breakpoint: "sm", collapsed: { mobile: !opened } }}
      aside={{
        width: 300,
        breakpoint: "md",
        collapsed: { desktop: false, mobile: true },
      }}
      padding="md"
    >
      <AppShell.Header>
        <Group h="100%" px="md">
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
          {/* <MantineLogo size={30} /> */}
          <div><IconAlignJustified />
          <Text color="#ff5733">Theme-1</Text>
          <IconCircleXFilled /></div>
          <IconAlignJustified />
          <Text color="#ff5733">Theme-1</Text>
          <IconCircleXFilled />
        </Group>
      </AppShell.Header>
      <AppShell.Navbar p="md">
        {/* Navbar
        {Array(15)
          .fill(0)
          .map((_, index) => (
            <Skeleton key={index} h={28} mt="sm" animate={false} />
          ))} */}
          <NavbarMinimal />
      </AppShell.Navbar>
      <AppShell.Main>
      <Center>
        <Image
          maw={740}
          mx="auto"
          radius="md"
          src="https://media.istockphoto.com/id/1309147753/vector/realistic-mobile-phone-mockup-template-isolated-stock-vector.jpg?s=612x612&w=0&k=20&c=llG65plOEQMKpWq0e04XRls2mufZa3mhh-pEnz5iGbU="
          alt="Random image"
        />
      </Center>
      </AppShell.Main>
      <AppShell.Aside p="md">
        <Demo />
      </AppShell.Aside>
    </AppShell>
  );
};

export default AppShellDemo;
