import React from "react";
import { useDisclosure } from "@mantine/hooks";
import { AppShell, Burger, Group, Text, Menu, MenuItem } from "@mantine/core";

const AppShellDemo = () => {
  const [opened, { toggle }] = useDisclosure();

  const headerStyles = {
    backgroundColor: 'var(--mantine-color-blue-4)', // Set the background color to indigo
    borderRadius: "0.5rem", // Apply rounded corners
    padding: "0.5rem", // Add padding to the header
  };

  const menuStyles = {
    display: "flex", // Display menu items horizontally
  };

  return (
    <AppShell header={{ height: 60 }} padding="md">
      <div style={{ backgroundColor: "blue" }} className="rounded-lg p-2">
        <AppShell.Header style={headerStyles}>
          <Group h="50%" px="md">
            <Text>Them-1</Text>
          </Group>
          <Group h="50%" px="md">
            {/* Mantine Menu with display: flex */}
            <Menu position="bottom" opened={opened} onClose={toggle} style={menuStyles}>
              <MenuItem onClick={() => console.log("Menu item 1 clicked")}>Menu Item 1</MenuItem>
              <MenuItem onClick={() => console.log("Menu item 2 clicked")}>Menu Item 2</MenuItem>
              <MenuItem onClick={() => console.log("Menu item 3 clicked")}>Menu Item 3</MenuItem>
            </Menu>
          </Group>

          {/* Burger */}
          <Burger opened={opened} onClick={toggle} hiddenFrom="sm" size="sm" />
        </AppShell.Header>
      </div>
    </AppShell>
  );
};

export default AppShellDemo;
