import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Slider,
  ColorPicker,
  Avatar,
  Group,
  Divider,
  Accordion,
  rem,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
} from "@tabler/icons-react";
const NavbarItem = () => {
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconSearch", component: <IconSearch /> },
    { name: "IconShoppingBag", component: <IconShoppingBag /> },
    { name: "IconUser", component: <IconUser /> },
  ];
  return (
    <Stack bg="gray.3" radious="xs">
      <Stack m="xs" bg="indigo.2">
        <Flex
          mih={50}
          bg="white"
          m="xs"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "25px" }}
        >
          {icons.map((icon, index) => (
            <ActionIcon
              radius="xl"
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${icon.component}`}
              onClick={() => handleIconClick(index, icon)}
            >
              {/* <Avatar>{icon.component}</Avatar> */}
              {icon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>
      <Divider size="xs" />

      <Accordion variant="contained">
        <Accordion.Item value="photos">
          <Accordion.Control
            icon={
              <IconPhoto
                style={{
                  color: "var(--mantine-color-red-filled",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Recent photos
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="print">
          <Accordion.Control
            icon={
              <IconPrinter
                style={{
                  color: "var(--mantine-color-blue-filled",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Print photos
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>

        <Accordion.Item value="camera">
          <Accordion.Control
            icon={
              <IconCameraSelfie
                style={{
                  color: "var(--mantine-color-teal-filled)",
                  width: rem(20),
                  height: rem(20),
                }}
              />
            }
          >
            Camera settings
          </Accordion.Control>
          <Accordion.Panel>Content</Accordion.Panel>
        </Accordion.Item>
      </Accordion>
    </Stack>
  );
};

export default NavbarItem;
