      <Grid.Col span={4}><Navbar /></Grid.Col>
      <Grid.Col span={4}><NavbarItem /></Grid.Col>
      <Grid.Col span={4}><AppBar /></Grid.Col>
      <Grid.Col span={4}>
        <ImageTest />
      </Grid.Col>
      <Grid.Col span={4}>
        <TextTemplate />
      </Grid.Col>
      <Grid.Col span={4}>
        <Drawer />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
