import React, { useState } from "react";
import { Grid, MantineProvider, createTheme } from "@mantine/core";
import "@mantine/core/styles.css";
import NavbarSegmented from "./views/components/SideNavbar/NavbarSegmented";
import ResponsivePhone from "./views/components/ResponsivePhone";
const theme = createTheme({
  fontFamily: "Open Sans, sans-serif",
  primaryColor: "cyan",
});

function App() {
  // const [isStack1Selected, setIsStack1Selected] = useState(false);
  // const [isStack2Selected, setIsStack2Selected] = useState(false);
  // const [selectedItem, setSelectedItem] = useState(null);

  // // Function to handle item selection
  // const handleItemClick = (item) => {
  //   setSelectedItem(item);
  // };

  const [selectAppBar, setSelectAppBar] = useState(false);
  const [selectAppBar2, setSelectAppBar2] = useState(false);
  const [selectedAppBarItem, setSelectedAppBarItem] = useState(null);

  // Function to update the selected item in the state
  const onItemClick = (item) => {
    setSelectedAppBarItem(item);
  };

  return (
    <MantineProvider theme={theme}>
      <Grid>
        <Grid.Col span={4}>
          <NavbarSegmented
            selectApp={selectAppBar}
            setSelect={setSelectAppBar}
            select2={selectAppBar2}
            setSelect2={setSelectAppBar2}
            onItemClick={onItemClick}
          />
        </Grid.Col>
        <Grid.Col span={4} mt="xs">
        <ResponsivePhone selectedAppBarItem={selectedAppBarItem} />
        </Grid.Col>
        <Grid.Col span={4}></Grid.Col>
      </Grid>
    </MantineProvider>
  );
}

export default App;
