import { Stack, Text, ColorPicker, Flex, Slider, Input } from "@mantine/core";
import React, { useState } from "react";

const Layout3 = ({ onColorChange, boxBorderRadius, onBorderRadiusChange, boxShadow, onBoxShadowChange, text, onTextChange }) => {
  // ... Existing code ...

  return (
    <Stack bg="gray.3">
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onColorChange(color);
          }}
        />
      </Stack>
      
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Box Shadow</Text>
        <Input
          type="text"
          value={boxShadow}
          onChange={(event) => {
            onBoxShadowChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Heading Text</Text>
        <Input
          type="text"
          value={text}
          onChange={(event) => {
            onTextChange(event.target.value);
          }}
          ml="xs"
          mr="xs"
          mb="xs"
        />
      </Stack>
    </Stack>
  );
};

export default Layout3;
