import React, { useState } from 'react';
import { Accordion, ActionIcon, Center } from '@mantine/core';
import { IconDots, IconMinus, IconPlus } from '@tabler/icons-react';

const Receipts = () => {
  const [openItem, setOpenItem] = useState(null);

  const toggleChevronIcon = (index) => {
    setOpenItem(openItem === index ? null : index);
  };

  const renderAccordionItem = (value, label, isParent) => (
    <Accordion.Item key={value} value={value} open={openItem === value}>
      <Accordion.Control
        icon={isParent ? (openItem === value ? <IconMinus size={`15px`} /> : <IconPlus size={`15px`} />) : null}
        onClick={() => toggleChevronIcon(value)}
      >
        {label}
      </Accordion.Control>
      <Accordion.Panel>
        {isParent && (
          <Accordion chevron={null}>
            <Accordion.Item value={`${value}-nested`}>
              <Accordion.Control>Test</Accordion.Control>
              <Accordion.Panel>Content</Accordion.Panel>
            </Accordion.Item>
          </Accordion>
        )}
      </Accordion.Panel>
    </Accordion.Item>
  );

  return (
    <Accordion chevron={null}>
      {renderAccordionItem('apple', 'Apple', true)}
      {renderAccordionItem('test', 'Test', false)}
    </Accordion>
  );
};

export default Receipts;
