import React, { useState } from "react";
import { useDisclosure } from "@mantine/hooks";
import { Grid } from "@mantine/core";
import ImageTest from "./ImageTest";
import TextTemplate from "./TextTemplate";
import Drawer from "./Drawer";
import Navbar from "./Navbar";
import NavbarItem from "./NavbarItem";
import ResponsivePhone from "./ResponsivePhone";
import AppBar from "../Global/AppBar";
import AccordionBar from "./AccordionBar";

const Index = () => {
  const disclosure = useDisclosure(); // Use useDisclosure in the Index component

  return (
    <Grid>
      <Grid.Col span={4}>
        <AccordionBar disclosure={disclosure} /> {/* Pass disclosure as a prop to AccordionBar */}
      </Grid.Col>
      <Grid.Col span={4}>
        <ResponsivePhone openDrawer={disclosure.isOpen} setOpenDrawer={disclosure.toggle} />
      </Grid.Col>
      {/* Other components... */}
      <Grid.Col span={4}>
        <Drawer isOpen={disclosure.isOpen} onClose={disclosure.close} />
      </Grid.Col>
    </Grid>
  );
};

export default Index;
