import React, { useState } from "react";
import {
  Flex,
  Stack,
  Text,
  ActionIcon,
  Accordion,
  Radio,
  Group,
  Center,
  ColorInput,
  Slider,
  Input,
  Button,
  ColorPicker,
} from "@mantine/core";
import {
  IconCircleXFilled,
  IconAlignLeft,
  IconAlignCenter,
  IconAlignJustified,
  IconAlignRight,
  IconAdjustmentsHorizontal,
  IconBaselineDensityMedium,
  IconBaselineDensitySmall,
} from "@tabler/icons-react";

const Drawer = () => {
  const [value, setValue] = useState(1);
  const [selectedIcon, setSelectedIcon] = useState(null);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  const handleIncrement = () => {
    const newValue = value + 1;
    if (newValue <= 100) {
      setValue(newValue);
    }
  };

  const handleIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedIcon(index);
  };

  const icons = [
    { name: "BaselineDensityMedium", component: <IconBaselineDensityMedium /> },
    { name: "BaselineDensitySmall", component: <IconBaselineDensitySmall /> },
    { name: "AlignCenter", component: <IconAlignCenter /> },
    { name: "AlignJustified", component: <IconAlignJustified /> },
    { name: "AlignRight", component: <IconAlignRight /> },
  ];

  return (
    <Stack bg="gray.3">
      <Flex
        bg="indigo.6"
        style={{
          borderRadius: "5px",
          display: "flex",
          height: "40px",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <Text
          ml="xs"
          color="white"
          style={{ display: "inline-block", justifyContent: "flex-center" }}
        >
          Drawer Icon
        </Text>
        <div
          style={{
            display: "flex",
            justifyContent: "flex-end",
            marginRight: "1rem",
          }}
        >
          <IconCircleXFilled />
        </div>
      </Flex>

      <Stack>
        <Text ml="xs">Change Icon</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          {icons.map((icon, index) => (
            <ActionIcon
              key={index}
              variant={selectedIcon === index ? "outline" : "default"}
              aria-label={`Icon ${icon.component}`}
              onClick={() => handleIconClick(index, icon)}
            >
              {icon.component}
            </ActionIcon>
          ))}
        </Flex>
      </Stack>

      <Stack>
        <Text ml="xs">Background</Text>
        {/* <ColorInput
          ml="xs"
          mr="xs"
          placeholder="Choose Background Colors"
          style={{ display: "inline-block" }}
        /> */}
        <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
      </Stack>

      <Stack>
        <Text ml="xs">Icon Color</Text>
        {/* <ColorInput
          ml="xs"
          mr="xs"
          placeholder="Choose Icon colors"
          style={{ display: "inline-block" }}
        /> */}
        <ColorPicker ml="xs" mr="xs" w="auto" size="lg" format="rgba" />
      </Stack>

      <Stack>
        <Text ml="xs">Size</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            my="xs"
            ml="xs"
            labelAlwaysOn
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack>
        <Text ml="xs">Weight</Text>
        <Flex
          mih={50}
          bg="white"
          ml="xs"
          mr="xs"
          gap="md"
          justify="center"
          align="center"
          direction="row"
          wrap="wrap"
          style={{ border: "1px solid #1b1c1b", borderRadius: "5px" }}
        >
          <Slider
            my="xs"
            ml="xs"
            labelAlwaysOn
            value={value}
            onChange={handleChange}
            min={0}
            max={100}
            style={{ width: "300px" }}
          />
        </Flex>
      </Stack>

      <Stack>
        <Text ml="xs">Back Button Radious</Text>
        <Flex
