import React from "react";
import { Box, Paper } from "@mantine/core";

const MobileFrame = ({ children }) => {
  return (
    <Box
      style={{
        width: '375px', // Adjust this width to match your desired mobile screen size
        height: '667px', // Adjust this height to match your desired mobile screen size
        margin: '0 auto', // Center the frame horizontally
        position: 'relative',
        border: '2px solid #000', // Border to mimic phone frame
        borderRadius: '16px', // Rounded corners for phone frame
        overflow: 'hidden', // Clip content within the frame
      }}
    >
      <Paper
        style={{
          width: '100%',
          height: '100%',
          borderRadius: 'inherit', // Inherit border-radius for inner content
        }}
        padding="md"
        shadow="xs"
      >
        {children}
      </Paper>
    </Box>
  );
};

export default MobileFrame;
