import React from "react";
import { Text, Box, Accordion } from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import CustomizeCategoryList1 from "./CustomizeCategoryList1";
import Settings from "./Settings";

const CategoryCustomize = ({ categoryListBoxVisible }) => {
  const elements = categoryListBoxVisible
    ? [
        // Box
        {
          type: Box,
          props: {
            m: "xs",
            h: "fit-content",
            bg: "gray.2",
            style: { borderRadius: "10px" },
          },
          children: [
            // Box (Header)
            {
              type: Box,
              props: {
                bg: "indigo.5",
                ml: "xs",
                mr: "xs",
                style: {
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "space-between",
                  backgroundColor: "indigo.5",
                  padding: "10px",
                  borderRadius: "10px",
                },
              },
              children: [
                // Text
                {
                  type: Text,
                  props: {
                    fw: 900,
                    c: "#FFFFFF",
                    style: {
                      flex: "1",
                      textAlign: "center",
                    },
                  },
                  children: "Customize",
                },
                // IconX
                {
                  type: IconX,
                  props: {
                    size: 28,
                    style: {
                      borderRadius: "50%",
                      background: "red",
                      color: "white",
                      alignSelf: "flex-end",
                    },
                  },
                },
              ],
            },
            // Accordion
            {
              type: Accordion,
              props: {
                m: "xs",
                variant: "contained",
                defaultValue: "category-item",
                pb: 10,
              },
              children: [
                // Accordion.Item
                {
                  type: Accordion.Item,
                  props: { value: "category-item" },
                  children: [
                    // Accordion.Control
                    {
                      type: Accordion.Control,
                      props: { bg: "#FFFFFF" },
                      children: <Text>Category Item</Text>,
                    },
                    // Accordion.Panel
                    {
                      type: Accordion.Panel,
                      props: { bg: "#FFFFFF" },
                      children: <CustomizeCategoryList1 />,
                    },
                  ],
                },
                // Settings
                {
                  type: Settings,
                },
              ],
            },
          ],
        },
      ]
    : [];

  return <>{elements.map(renderElement)}</>;
};

function renderElement(element, index) {
  const { type, props, children } = element;
  return React.createElement(type, { ...props, key: index }, children);
}

export default CategoryCustomize;
