import {
  Avatar,
  Container,
  Flex,
  Stack,
  Autocomplete,
  Box,
  Button,
} from "@mantine/core";
import React, { useState } from "react";
import {
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";
const AppBar = () => {
  const [isSelected, setIsSelected] = useState(false);

  const handleContainerClick = () => {
    setIsSelected(!isSelected);
  };

  return (
    <Flex
      mih={50}
      w
      bg="rgba(0, 0, 0, .3)"
      gap="md"
      justify="center"
      align="center"
      direction="column"
      wrap="wrap"
      radius="xl"
    >
      <Container
        fluid
        bg="indigo"
        style={{
          borderRadius: "5px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
          height: "40px",
        }}
      >
        <div style={{ display: "inline-block", justifyContent: "flex-center" }}>
          App Bars
        </div>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled color="red" />
        </div>
      </Container>
      <Stack
        m="xs"
        bg="var(--mantine-color-white)"
        style={{ display: "flex", alignItems: "center", width: "100%" }}
      >
        <Flex mih={50} gap="md" justify="space-between" align="center">
          <div style={{ flex: 1 }}>
            <IconAlignLeft style={{ marginLeft: "4px" }} />
          </div>

          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "md",
              marginRight: "4px",
            }}
          >
            <IconSearch />
            <Avatar
              variant="filled"
              radius="sm"
              src="https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=250&q=80"
            />
          </div>
        </Flex>
      </Stack>
      {/* <Stack
        style={{ display: "flex", alignItems: "center", width: "100%" }}
        bg="var(--mantine-color-blue-light)"
      >
        <div style={{ flex: 1 }}>App Bars</div>
        <div style={{ alignSelf: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Stack> */}

      <Button mb="xs">Button 3</Button>
    </Flex>
  );
};

export default AppBar;
