import React, { useState } from "react";
import { Flex, Accordion, ActionIcon, Stack } from "@mantine/core";
import { IconColumns1 } from "@tabler/icons-react";

const CustomizeCategory = () => {
  const [selectionStates, setSelectionStates] = useState([true, false, false, false, false, false]);

  const toggleSelection = (index) => {
    const newSelectionStates = selectionStates.map((_, i) => i === index);
    setSelectionStates(newSelectionStates);
  };

  const accordionItems = [
    {
      title: "Type",
      controls: ["Type"],
      icons: [
        {
          variant: selectionStates[0] ? "filled" : "default",
          width: 150,
          height: 50,
        },
        {
          variant: selectionStates[1] ? "filled" : "default",
          width: 100,
          height: 50,
          marginLeft: "xs",
        },
        {
          variant: selectionStates[2] ? "filled" : "default",
          width: 50,
          height: 50,
          marginLeft: "xs",
        },
        {
          variant: selectionStates[3] ? "filled" : "default",
          width: 150,
          height: 100,
        },
        {
          variant: selectionStates[4] ? "filled" : "default",
          width: 100,
          height: 100,
          marginLeft: "xs",
        },
        {
          variant: selectionStates[5] ? "filled" : "default",
          width: 50,
          height: 100,
          marginLeft: "xs",
        },
      ],
    },
    // Add more items as needed
  ];

  return (
    <>
      {accordionItems.map((item, index) => (
        <Accordion.Item key={index} value={item.controls[0]}>
          <Accordion.Control bg="#FFFFFF">{item.controls[0]}</Accordion.Control>
          <Accordion.Panel bg="#FFFFFF">
            {item.icons.map((icon, i) => (
              <ActionIcon
                key={i}
                variant={icon.variant}
                onClick={() => toggleSelection(i)}
                color="indigo"
                w={icon.width}
                h={icon.height}
                ml={icon.marginLeft}
              >
                <Stack w="100%" gap={0}>
                  <Flex>
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                    <IconColumns1 style={{ width: "100%", height: "100%" }} stroke={1} />
                  </Flex>
                </Stack>
              </ActionIcon>
            ))}
          </Accordion.Panel>
        </Accordion.Item>
      ))}
    </>
  );
};

export default CustomizeCategory;
