import React, { useState } from "react";
import {
  Text,
  Flex,
  Accordion,
  Checkbox,
  ColorPicker,
  Input,
  Image,
} from "@mantine/core";

import { Dropzone, IMAGE_MIME_TYPE } from "@mantine/dropzone";

const CustomizeCategoryList1 = () => {
  const accordionItems = [
    {
      value: "electronics",
      label: "Electronics",
      level: "electronic_level",
    },
    {
      value: "accessories",
      label: "Accessories",
      level: "accessories_level",
    },
    {
      value: "clothing",
      label: "Clothing",
      level: "clothing_level",
    },
    {
      value: "shoes",
      label: "Shoes",
      level: "shoes_level",
    },
  ];

  const initialFilesState = accordionItems.map(() => []);

  const [filesStates, setFilesStates] = useState(initialFilesState);

  const previews = (index) => {
    return filesStates[index].map((file, fileIndex) => {
      const imageUrl = URL.createObjectURL(file);
      return (
        <Image
          key={fileIndex}
          src={imageUrl}
          onLoad={() => URL.revokeObjectURL(imageUrl)}
        />
      );
    });
  };

  const [categoryHeader, setCategoryHeader] = useState({
    electronic_level: "Electronics",
    accessories_level: "Accessories",
    clothing_level: "Clothing",
    shoes_level: "Shoes",
  });

  const updateProperty = (property, value) => {
    setCategoryHeader({ ...categoryHeader, [property]: value });
  };

  const onLevelChange = (level, text) => {
    updateProperty(level, text);
  };

  const onImageDrop = (index, files) => {
    const updatedFilesStates = [...filesStates];
    updatedFilesStates[index] = files;
    setFilesStates(updatedFilesStates);
  };

  const [files, setFiles] = useState([]);

  const previewsaddmore = files.map((file, index) => {
    const imageUrl = URL.createObjectURL(file);
    return (
      <Image
        key={index}
        src={imageUrl}
        onLoad={() => URL.revokeObjectURL(imageUrl)}
      />
    );
  });

  return (
    <Accordion m="xs" variant="contained">
      <Accordion.Item value="appbar">
        <Accordion.Control bg="gray.2">AppBar</Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text mt="xs">Background Color</Text>
            <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategoryList1;
