    //     <Layout
    //       bgColor={bgColor}
    //       borderRadius={boxBorderRadius}
    //       boxShadow={boxShadow}
    //       text={text}
    //       textBgColor={textBgColor}
    //       textBorderRadius={textBorderRadius}
    //       columns={columns}
    //       itemboxorderRadius={itemBoxBorderRadius}
    //     />
    //   </Grid.Col>
    //   <Grid.Col span={4}>
    //     <Layout2 />
    //   </Grid.Col>
    //   <Grid.Col span={4}>
    //     <Layout3
    //       onColorChange={handleColorChange}
    //       boxBorderRadius={boxBorderRadius}
    //       onBorderRadiusChange={handleBorderRadiusChange}
    //       boxShadow={boxShadow}
    //       onBoxShadowChange={handleBoxShadowChange}
    //       text={text}
    //       onTextChange={handleTextChange}
    //       textBgColor={textBgColor}
    //       onTextBgColorChange={handleTextBgColorChange}
    //       textBorderRadius={textBorderRadius}
    //       onTextBorderRadiusChange={handleTextBorderRadiusChange}
    //       columns={columns}
    //       onColumnsChange={handleColumnsChange}
    //       itemBoxBorderRadius={itemBoxBorderRadius}
    //       onItemBoxBorderRadiusChange={handleItemBoxBorderRadiusChange}
    //     />
    //   </Grid.Col>
    // </Grid>
  );
};

export default Index;
