import React from "react";
import { Box, Text, Accordion } from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import HomeSlider from "./HomeSlider";
import CategoryList from "./CategoryList";
import BrandList from "./BrandList";
import BrandWithProduct from "./BrandWithProduct";
import LatestProduct from "./LatestProduct";
import MostPopulerProduct from "./MostPopulerProduct";
import CategoryWithProduct from "./CategoryWithProduct";

const Layout = () => {
const [layouttVisible, setLayoutVisible] = useState(true);
return (
    <Box m="xs" bg="gray.2" style={{ borderRadius: "10px" }}>
      <Box
        bg="indigo.5"
        ml="xs"
        mr="xs"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "indigo.5",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <Text
          fw={900}
          c={`#FFFFFF`}
          style={{
            flex: "1",
            textAlign: "center",
          }}
        >
          Layouts
        </Text>
        <IconX
          size={28}
          style={{
            borderRadius: "50%",
            background: "red",
            color: "white",
            alignSelf: "flex-end",
            cursor: "pointer",
          }}
        />
      </Box>

      <Accordion m="xs" pb={10} variant="contained">
        {/* Slider */}
        <HomeSlider />
        {/* Category List */}
        <CategoryList />
        {/* Category With Product */}
        <CategoryWithProduct />
        {/* Brand List */}
        <BrandList />
        {/* Brand With Product */}
        <BrandWithProduct />
        {/* Latest Product/ Recently Added */}
        <LatestProduct />
        {/* Most Populer Product */}
        <MostPopulerProduct />
      </Accordion>
    </Box>
  );
};

export default Layout;
