import { Box, Center, Grid, Stack, Text } from "@mantine/core";
import React, { useState } from "react";

const customStyles = {
  box: {
    borderRadius: "20px",
    background: "#F0F0F0",
    boxShadow: "0 6px 8px rgba(0, 0, 0, 0.2)",
    height: 250,
  },
  headerBox: {
    background: "#FF5733",
    borderRadius: "20px 20px 0 0",
    cursor: "pointer",
  },
  itemBox: {
    background: "#E5E5E5",
    border: "1px solid #333333",
    cursor: "pointer",
    height: 40,
  },
};

const Layout = ({ selected, onItemSelect, customStyles }) => {
  const items = ["Item 1", "Item 2", "Item 3"];
  const [isTextBoxSelected, setTextBoxSelected] = useState(false);

  const toggleTextBoxSelection = () => {
    setTextBoxSelected(!isTextBoxSelected);
  };

  return (
    <Box style={{ ...customStyles.box }}>
    <Stack spacing={0}>
      <Box
        bg={isTextBoxSelected ? "blue.5" : "lime.5"}
        style={{ ...customStyles.headerBox }}
        onClick={toggleTextBoxSelection}
      >
        <Center>
          <Text>Test</Text>
        </Center>
      </Box>
      <Grid ml="xs" mr="xs">
        {items.map((item, index) => (
          <Grid.Col span={4} key={index}>
            <Box
              bg={selected === item ? "blue.1" : "white"}
              style={{ ...customStyles.itemBox }}
              onClick={() => onItemSelect(item)}
            >
              <Center>{item}</Center>
            </Box>
          </Grid.Col>
        ))}
      </Grid>
    </Stack>
  </Box>
  );
};

export default Layout;
